mapper.update:
update t_user 
set username=#{user.username}, nickname=#{user.nickname}, password=#{user.password}
where id=#{user.id};

mapper.select:
select *
from t_user
where username like #{user.username} and nickname like #{user.nickname};

select count(*)
from t_user
where username like #{user.username} and nickname like #{user.nickname};
